package Pattern1;

public class Pattern4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int row= 6;
		
		for(int i=1; i<row;i++) 
		{
		
			
			for(int j=0; j<i;j++) {
				System.out.print("*");
				
			}
			System.out.println();  
		}
	}
	}


